token.reduce:
function (keys, values, rereduce) {
    if (rereduce) {
        var result = {
            count: values[0].count,
            ade: values[0].ade,
            mel: values[0].mel,
            syd: values[0].syd,
            can: values[0].can,
            bri: values[0].bri,
            hob: values[0].hob,
            per: values[0].per,
           // unknown:values[0].unknown


        };
        for (var i = 1; i < values.length; i++) {
            result.count = result.count + values[i].count;
            result.ade = result.ade + values[i].ade;
            result.mel= result.mel+values[i].mel;
            result.syd=result.syd+values[i].syd;
            result.can=result.can+values[i].can;
            result.bri=result.bri+values[i].bri;
            result.hob=result.hob+values[i].hob;
            result.per=result.per+values[i].per;
            //result.unknown=result.unknown+values[i].unknown;

        }
        return result
    }
    // Non-rereduce case
    var result = {
        count: 0,
        ade: 0,
        mel: 0,
        syd: 0,
        can: 0,
        bri: 0,
        hob: 0,
        per: 0,
        //unknown:0




    }

    var Mel_list = [144.5532, -38.2250, 145.5498, -37.5401];
    var Syd_list = [150.6396, -34.1399, 151.3439, -33.5780];
    var Can_list = [148.9960, -35.4799, 149.3993, -35.1244];
    var Bri_list = [152.6859, -27.6633, 153.4685, -27.0220];
    var Ade_list = [138.4421, -35.3490, 138.7832, -34.6481];
    var Hob_list = [147.1762, -42.9619, 147.4628, -42.6999];
    var Per_list = [115.5607, -32.4824, 116.4151, -31.4552];
    // add list...
    for (var i = 0; i < keys.length; i++) {

        if(values[i].z.indexOf('Mel') !=-1){
          result.mel = result.mel + 1
        }
        else if(values[i].z.indexOf('Syd') !=-1){
          result.syd = result.syd + 1
        }
        else if(values[i].z.indexOf('Hob') !=-1){
          result.hob = result.hob + 1
        }
      else if(values[i].z.indexOf('Per') !=-1){
          result.per = result.per + 1
        }
        else if(values[i].z.indexOf('Can') !=-1){
          result.can = result.can + 1
        }
        else if(values[i].z.indexOf('Bri') !=-1){
          result.bri = result.bri + 1
        }
        else if(values[i].z.indexOf('Ade') !=-1){
          result.ade = result.ade + 1
        }
        // else{
        //   result.unknown= result.unknown+1
        // }
        result.count = result.count + 1
    }
    return result
}

token.map:
function (doc) {
    doc_x1 = doc.place.bounding_box.coordinates[0][0][0];
    doc_y1 = doc.place.bounding_box.coordinates[0][0][1];
    doc_x2 = doc.place.bounding_box.coordinates[0][2][0];
    doc_y2 = doc.place.bounding_box.coordinates[0][1][1];
    x = (doc_x1 + doc_x2) / 2;
    y = (doc_y1 + doc_y2) / 2;
    z = doc.place.full_name;

    var emojis = ['ðŸ”', 'ðŸŸ', 'ðŸ•', 'ðŸŒ­', 'ðŸ°', 'ðŸ«', 'ðŸ¦', 'ðŸ¨', 'ðŸ©', 'ðŸº', 'ðŸ»', 'ðŸ˜‹', 'ðŸ¤¤', 'ðŸŽ‚', 'ðŸ·', 'ðŸ¬', 'ðŸª', 'ðŸ—', 'ðŸœ']
    var token_list = ['Burger', 'Fast food', 'Fish and chips', 'Ice cream', 'Junk food', 'Beer', 'Soft drink', 'KFC', 'Alcohol', 'McDonald', 'Hungry Jack', 'Chocolate', 'Cake', 'Doughnut', 'Cheese', 'Fried chicken', 'Donut', 'Dessert', 'Candy', 'Bubble Tea','Fries','Nugget','Pie','Sundae','Macarons','bakery','snack','chips','drunk','feast','starving']
    for (var i = 0; i < token_list.length; i++) {
        token_list[i] = token_list[i].toLowerCase()
    }
    var all = token_list.concat(emojis)
    for (var i = 0; i < all.length; i++) {
        if (doc.text.toLowerCase().indexOf(all[i]) != -1) {
            emit(doc.text, { x: x, y: y, z: z });
            break;
        }
    }
}

